import React, { Component } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from 'redux';
import { Route, Link, Switch } from "react-router-dom";
import { movies } from "../actions/action"

class Movies extends Component {
    render() {
        return(
            <div className="col-md-8 d-flex flex-col">
               {this.props.movies.map((elem, index)=>{
                   return <div className="card w-100">
                       <img className="card-img-top" src={`http://image.tmdb.org/t/p/w780${elem.poster_path}`} alt="Card image cap"/>
                        <div className="card-body"></div>  
                   </div>
               })}
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    console.log("home state", state)
    return {
        movies: state.movies
    }
}

export default connect(mapStateToProps)(Movies)
