let initialState = {
    movies: [],
    tvShows: [],
    movieData: [],
    showData: [],
    loadMovies: [],
    count: 1
}

function appReducer(state = initialState, action) {
    let stateCopy = JSON.parse(JSON.stringify(state))
    switch (action.type) {
        case "movieList":
            stateCopy.movies = action.payload;
            return stateCopy
        case "tvList":
            stateCopy.tvShows = action.payload;
            return stateCopy
        case "setMovieIndex":
            stateCopy.movieData = stateCopy.movies[action.payload];
            return stateCopy
        case "setShowIndex":
            stateCopy.showData = stateCopy.tvShows[action.payload];
            return stateCopy
            case "pageChange":
                return changePage(stateCopy, action.payload)
        default:
            return stateCopy
    }
}

export default appReducer;


let changePage = (state, num)=> {
    let pageId = 1
    if(num > this.state.count){
    pageId++
    this.state.count = pageId;
    }
    return state
}
