import React, { Component } from "react";
import axios from "axios";
import { connect } from "react-redux";
import { bindActionCreators } from 'redux';
import { Link } from "react-router-dom";
import { setMovieIndex } from "../actions/action"
import "../App.css"

class Movies extends Component {

    state = {
        count: 1
    }

    request(){
      let req = axios({
              method: "get",
              url: `https://api.themoviedb.org/3/movie/popular?api_key=cf672d5af5b5025e48ee79a16a4e6a9f&language=en-US&page=${this.state.count}`
          })
          req.then((res)=>console.log("new api call", res))
    }

    handleClick(){
        this.setState({
            count: this.state.count + 1
        })
        this.request()
    }
    render() {
        console.log("local",this.state)
        return (
            <div>
                <div className="row col m-4">
                    {this.props.movies.map((elem, index) => {
                        return <Link style={{ textDecoration: 'none', color: 'white' }} to="/movieposter"> <div key={index} className="card" onClick={() => this.props.setMovieIndex(index)}>
                            <img className="card-img-top p-2" src={`http://image.tmdb.org/t/p/w780${elem.poster_path}`} alt="" />
                            <div className="card-body"><small className="title">{elem.title}</small></div>
                        </div>
                        </Link>
                    })}
                    <div>
                    <button onClick={()=>this.handleClick(this)} className="btn btn-warning">Load More</button>
                    </div>
                    {this.props.movies.map((elem, index) => {
                        return <Link style={{ textDecoration: 'none', color: 'white' }} to="/movieposter"> <div key={index} className="card" onClick={() => this.props.setMovieIndex(index)}>
                            <img className="card-img-top p-2" src={`http://image.tmdb.org/t/p/w780${elem.poster_path}`} alt="" />
                            <div className="card-body"><small className="title">{elem.title}</small></div>
                        </div>
                        </Link>
                    })}
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    console.log(state)
    return {
        movies: state.movies
    }
}

const mapDispatchToProps = (dispatch) => {
    return bindActionCreators({
        setMovieIndex
    }, dispatch)
}

export default connect(mapStateToProps, mapDispatchToProps)(Movies)
