let initialState = {
    movies: [],
    tvShows: [],
    videoData: []
}

function appReducer(state = initialState, action) {
  let stateCopy = JSON.parse(JSON.stringify(state))
  switch (action.type) {
            case "movieList":
                addToMovies(stateCopy.movies , action.payload)
            return stateCopy
        default:
      return stateCopy
  }
}

export default appReducer;

let addToMovies = (state, data)=> {
    let newMovies = state.movies.slice()
    newMovies.push(data)
    state.movies = newMovies;
    return state;
  }
