import React, { Component } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from 'redux';
import { Link } from "react-router-dom";
import { setShowIndex } from "../actions/action"
import "../App.css"

class TvShows extends Component {
    render() {
        return (
            <div>
                <div className="row mainDiv col">
                    {this.props.tvshows.map((elem, index) => {
                        return <Link style={{ textDecoration: 'none', color: 'white' }} to="/showposter" exact> <div key={index} className="card" onClick={() => this.props.setShowIndex(index)}>
                            <img className="card-img-top p-2" src={`http://image.tmdb.org/t/p/w780${elem.poster_path}`}/>
                            <div className="card-body"><small className="title">{elem.name}</small></div>
                        </div>
                        </Link>
                    })}
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    console.log("tvShows state", state)
    return {
        tvshows: state.tvShows,
    }
}

const mapDispatchToProps = (dispatch) => {
    return bindActionCreators({
        setShowIndex
    }, dispatch)
}

export default connect(mapStateToProps, mapDispatchToProps)(TvShows)
