import React, { Component } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from 'redux';
import { Route, Link, Switch } from "react-router-dom";
import { setPosterId } from "../actions/action"
import "../App.css"

class Movies extends Component {
    render() {
        return (
            <div>
                <div className="row mainDiv col">
                    {this.props.movies.map((elem, index) => {
                        return <div className="card" onClick={() => this.props.setPosterId(elem.id)}>
                            <img className="card-img-top p-2" src={`http://image.tmdb.org/t/p/w780${elem.poster_path}`} alt="Card image cap" />
                            <div className="card-body"><small className="title">{elem.title}</small></div>
                        </div>
                    })}
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    console.log("movies state", state)
    return {
        movies: state.movies,
        id: state.posterId
    }
}

const mapDispatchToProps = (dispatch) => {
    return bindActionCreators({
        setPosterId
    }, dispatch)
}

export default connect(mapStateToProps, mapDispatchToProps)(Movies)
