import React, { Component } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from 'redux';
import axios from "axios"
import { Route, Link, Switch } from "react-router-dom";
import { setPosterId } from "../actions/action"
import "../App.css"

class PosterPage extends Component {
    render() {
        return (
            <div>
                <div className="col-md-8">
                    <div className="row">
                        <div className="col-md-3">
                            <span className="text-white">hello</span>
                            {/* <img src={`http://image.tmdb.org/t/p/w780${elem.poster_path}`} alt="" /> */}
                        </div>

                    </div>
                    {/* {this.props.tvshows.map((elem, index) => {
                        return <div className="card" onClick={() => this.props.setPosterId(elem.id)}>
                            <img className="card-img-top p-2" src={`http://image.tmdb.org/t/p/w780${elem.poster_path}`} alt="Card image cap" />
                            <div className="card-body"><small className="title">{elem.name}</small></div>
                        </div>
                    })} */}
                </div>
            </div>
        )
    }
    componentDidMount(){
        (data)=>{
        return axios({
                 method: "get",
                 url: `https://api.themoviedb.org/3/movie/${this.props.id}?api_key=cf672d5af5b5025e48ee79a16a4e6a9f`,
                 data: data
             }, ()=>console.log("new api", data))  
         }
    }
}

const mapStateToProps = (state) => {
    console.log("posterpage state", state)
    return {
        tvshows: state.tvShows,
        id: state.posterId
    }
}

const mapDispatchToProps = (dispatch) => {
    return bindActionCreators({
        setPosterId
    }, dispatch)
}

export default connect(mapStateToProps, mapDispatchToProps)(PosterPage)
