let initialState = {
    movies: [],
    tvShows: [],
    videoData: [],
    posterId: ""
}

function appReducer(state = initialState, action) {
    let stateCopy = JSON.parse(JSON.stringify(state))
    switch (action.type) {
        case "movieList":
            stateCopy.movies = action.payload;
            return stateCopy
        case "tvList":
            stateCopy.tvShows = action.payload;
            return stateCopy
        case "SetPosterID":
            stateCopy.posterId = action.payload;
            return stateCopy
        default:
            return stateCopy
    }
}

export default appReducer;
