import React, { Component } from 'react';
import { connect } from "react-redux";
import { addProduct } from '../actions/action';
import { bindActionCreators } from 'redux';

class AddProduct extends Component {
  state = {
    ShopName: "",
    Area: "Jayanagar",
    Category: "Grocery",
    OpeningDate: "",
    ClosingDate: "",
  }

  addData = () => {
    let shopData = {
      "data": {
        "ShopName": this.state.ShopName,
        "Area": this.state.Area,
        "Category": this.state.Category,
        "OpeningDate": this.state.OpeningDate,
        "ClosingDate": this.state.ClosingDate,
      }
    }
    this.props.addProduct(shopData)
  }


  handleOnSubmit = (e) => {
    e.preventDefault()
    this.addData()
  }
  render() {



    return (
      <div>
        <br /><br />
        {
          <form class="form-group col-md-8 offset-md-2" onSubmit={this.handleOnSubmit}>
            <div className="d-flex flex-col">
              <input required type="text" className="form-control" placeholder="Shop Name" onChange={(elem) => this.setState({ ShopName: elem.target.value })} />
              <label for="exampleFormControlSelect1">Category</label>
              <select required className="form-control" id="exampleFormControlSelect1" onChange={(elem) => this.setState({ Area: elem.target.value })}>
                                <option>Search by category</option>
                                <option value="Mobiles">Mobiles</option>
                                <option value="Laptops">Laptops</option>
                                <option value="Desktops">Desktops</option>
                                <option value="Furniture">Furniture</option>
                                <option value="Cutlery">Cutlery</option>
                                <option value="Automobiles">Automobiles</option>
                                <option value="Books">Books</option>
                                <option value="Clothes">Clothes</option>
              </select>
              <label for="exampleFormControlSelect1">Condition</label>
              <select required className="form-control" id="exampleFormControlSelect1" onChange={(elem) => this.setState({ Category: elem.target.value })}>
                            <option value="Mint">Mint</option>
                            <option value="Very Good">Very Good</option>
                            <option value="Good">Good</option>
                            <option value="Decent">Decent</option>
                            <option value="Poor">Poor</option>
                            <option value="Very Poor">Very Poor</option>
              </select>
              <input required type="date" className="form-control" onChange={(elem) => this.setState({ OpeningDate: elem.target.value })} />
              <input required type="date" className="form-control" onChange={(elem) => this.setState({ ClosingDate: elem.target.value })} />
              <button className="form-control btn btn-danger">Submit</button>
            </div>
          </form>
        }
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  console.log("current state", state)
  return {
  }
}

const mapDispatchToProps = (dispatch) => {
  return bindActionCreators({ addProduct }, dispatch)
}


export default connect(mapStateToProps, mapDispatchToProps)(AddProduct)
