import React, { Component } from 'react';
import { connect } from "react-redux";
import { addProduct } from '../actions/action';
import { bindActionCreators } from 'redux';

class AddProduct extends Component {
  state = {
    ShopName: "",
    Area: "Jayanagar",
    Category: "Grocery",
    OpeningDate: "",
    ClosingDate: "",
  }

  addData = () => {
    let shopData = {
      "data": {
        "ShopName": this.state.ShopName,
        "Area": this.state.Area,
        "Category": this.state.Category,
        "OpeningDate": this.state.OpeningDate,
        "ClosingDate": this.state.ClosingDate,
      }
    }
    this.props.addProduct(shopData)
  }


  handleOnSubmit = (e) => {
    e.preventDefault()
    this.addData()
  }
  render() {



    return (
      <div>
        <br /><br />
        {
          <form class="form-group col-md-8 offset-md-2" onSubmit={this.handleOnSubmit}>
            <div className="d-flex flex-col">
              <input required type="text" className="form-control" placeholder="Shop Name" onChange={(elem) => this.setState({ ShopName: elem.target.value })} />
              <label for="exampleFormControlSelect1">Area</label>
              <select required class="form-control" id="exampleFormControlSelect1" onChange={(elem) => this.setState({ Area: elem.target.value })}>
                <option value="Jayanagar">Jayanagar</option>
                <option value="Basavanagudi">Basavanagudi</option>
                <option value="JP Nagar">JP Nagar</option>
                <option value="Padmanabhanagar">Padmanabhanagar</option>
                <option value="Banashankari">Banashankari</option>
                <option value="Uttarahalli">Uttarahalli</option>
                <option value="Kumaraswamy">Kumaraswamy</option>
                <option value="Girinagar">Girinagar</option>
              </select>
              <label for="exampleFormControlSelect1">Category</label>
              <select required class="form-control" id="exampleFormControlSelect1" onChange={(elem) => this.setState({ Category: elem.target.value })}>
                <option value="Grocery">Grocery</option>
                <option value="Butcher">Butcher</option>
                <option value="Baker">Baker</option>
                <option value="Chemist">Chemist</option>
                <option value="Stationery shop">Stationery shop</option>
                <option value="Hardware shop">Hardware shop</option>
                <option value="Flower">Flower</option>
                <option value="Newsagent">Newsagent</option>
              </select>
              <input required type="date" className="form-control" onChange={(elem) => this.setState({ OpeningDate: elem.target.value })} />
              <input required type="date" className="form-control" onChange={(elem) => this.setState({ ClosingDate: elem.target.value })} />
              <button className="form-control btn btn-danger">Submit</button>
            </div>
          </form>
        }
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  console.log("current state", state)
  return {
  }
}

const mapDispatchToProps = (dispatch) => {
  return bindActionCreators({ addProduct }, dispatch)
}


export default connect(mapStateToProps, mapDispatchToProps)(AddProduct)
