                <div className="d-flex flex-col col-md-8 offset-md-2">
                    <label for="exampleFormControlSelect1">Area</label>
                    <select onChange={(e) => this.setState({ Area: e.target.value })} class="form-control" id="exampleFormControlSelect1">
                        <option value="Jayanagar">Jayanagar</option>
                        <option value="Basavanagudi">Basavanagudi</option>
                        <option value="JP Nagar">JP Nagar</option>
                        <option value="Padmanabhanagar">Padmanabhanagar</option>
                        <option value="Banashankari">Banashankari</option>
                        <option value="Uttarahalli">Uttarahalli</option>
                        <option value="Kumaraswamy">Kumaraswamy</option>
                        <option value="Girinagar">Girinagar</option>
                    </select>
                    <label for="exampleFormControlSelect1">Category</label>
                    <select onChange={(e) => this.setState({ Category: e.target.value })} class="form-control" id="exampleFormControlSelect1">
                        <option value="Grocery">Grocery</option>
                        <option value="Butcher">Butcher</option>
                        <option value="Baker">Baker</option>
                        <option value="Chemist">Chemist</option>
                        <option value="Stationery shop">Stationery shop</option>
                        <option value="Hardware shop">Hardware shop</option>
                        <option value="Flower">Flower</option>
                        <option value="Newsagent">Newsagent</option>
                    </select>
                    <label for="exampleFormControlSelect1">Status</label>
                    <select onChange={(e) => this.setState({ Status: e.target.value })} class="form-control" id="exampleFormControlSelect1">
                        <option value="All">All</option>
                        <option value="Open">Open</option>
                        <option value="Close">Close</option>
                    </select>
                </div>
                <div>
                    <table className="table">
                        <thead>
                            <th>Shop Name</th>
                            <th>Shop Area</th>
                            <th>Shop Category</th>
                            <th>Shop Status</th>
                        </thead>
                        <tbody>
                            {/* {this.shopsFilter(this.props.shops).map((elem, index) => {
