    render() {

        return (
            <div>
                <div className="d-flex flex-col col-md-8 offset-md-2">
                    <label for="exampleFormControlSelect1">City</label>
                    <select required className="form-control" id="exampleFormControlSelect1" onChange={(elem) => this.setState({ City: elem.target.value })}>
                        <option value="Bengaluru">Bengaluru</option>
                        <option value="Mumbai">Mumbai</option>
                        <option value="Chennai">Chennai</option>
                        <option value="Kolkata">Kolkata</option>
                        <option value="Vadodara">Vadodara</option>
                        <option value="Agra">Agra</option>
                        <option value="Delhi">Delhi</option>
                        <option value="Patna">Patna</option>
                    </select>
                    <select required className="form-control" id="exampleFormControlSelect1" onChange={(elem) => this.setState({ Category: elem.target.value })}>
                        <option value="Mobiles">Mobiles</option>
                        <option value="Laptops">Laptops</option>
                        <option value="Desktops">Desktops</option>
                        <option value="Furniture">Furniture</option>
                        <option value="Cutlery">Cutlery</option>
                        <option value="Automobiles">Automobiles</option>
                        <option value="Books">Books</option>
                        <option value="Clothes">Clothes</option>
                    </select>
                    <label for="exampleFormControlSelect1">Status</label>
                    <input required type="number" className="form-control" placeholder="Price" onChange={(elem) => this.setState({ Price: elem.target.value })} />
                </div>
                <br />
                <div className="container">
                    <div className="shadow">
                        <table className="table">
                            <thead className="bg-dark text-white text-center">
                                <th>Product Name</th>
                                <th>Product Category</th>
                                <th>Product Price</th>
                                <th>Product Condition</th>
                            </thead>
                            <tbody>
                                {/* {this.shopsFilter(this.props.shops).map((elem, index) => {
                                if(elem.status === "Open") {
                                return <tr className="alert alert-success">
                                    <td>{elem.data.ShopName}</td>
                                    <td>{elem.data.Area}</td>
                                    <td>{elem.data.Category}</td>
                                    <td>{elem.status}</td>
                                </tr>
                                }
                                else {
                                    return <tr className="alert alert-danger">
                                    <td>{elem.data.ShopName}</td>
                                    <td>{elem.data.Area}</td>
                                    <td>{elem.data.Category}</td>
                                    <td>{elem.status}</td>
                                </tr>
                                }
                            })} */}
                            </tbody>

                        </table>

                    </div>

                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    console.log("current state", state)
    return {
        shops: state.appReducer.ShopList
    }
}


export default connect(mapStateToProps)(ProductList)
