import React, { Component } from 'react';
import { connect } from "react-redux";
import { addProduct } from '../actions/action';
import { bindActionCreators } from 'redux';

class AddProduct extends Component {
  state = {
    ProductName: "",
    Category: "Mobiles",
    Condition: "Mint",
    Bargain: false,
    Price: "",
    City: "Bengaluru"
  }


  addData = () => {
    let shopData = {
      "data": {
        "ProductName": this.state.ProductName,
        "Category": this.state.Category,
        "Condition": this.state.Condition,
        "Bargain": this.state.Bargain,
        "Price": this.state.Price,
        "City": this.state.City,
      }
    }
    this.props.addProduct(shopData)
  }


  handleOnSubmit = (e) => {
    e.preventDefault()
    this.addData()
  }
  render() {
    return (
      <div>
        <br /><br />
        <div className="container">
          {
            <form class="form-group col-md-8" onSubmit={this.handleOnSubmit}>
              <div className="d-flex flex-col">
                <div>
                  <strong>Product</strong>
                <input required type="text" className="form-control" placeholder="Product Name" onChange={(elem) => this.setState({ ProductName: elem.target.value })} />
                </div>
                <div for="exampleFormControlSelect1">
                  <strong>Category</strong>
                <select required className="form-control" id="exampleFormControlSelect1" onChange={(elem) => this.setState({ Category: elem.target.value })}>
                  <option value="Mobiles">Mobiles</option>
                  <option value="Laptops">Laptops</option>
                  <option value="Desktops">Desktops</option>
                  <option value="Furniture">Furniture</option>
                  <option value="Cutlery">Cutlery</option>
                  <option value="Automobiles">Automobiles</option>
                  <option value="Books">Books</option>
                  <option value="Clothes">Clothes</option>
                </select>
                </div>
                <label for="exampleFormControlSelect1">Condition</label>
                <select required className="form-control" id="exampleFormControlSelect1" onChange={(elem) => this.setState({ Condition: elem.target.value })}>
                  <option value="Mint">Mint</option>
                  <option value="Very Good">Very Good</option>
                  <option value="Good">Good</option>
                  <option value="Decent">Decent</option>
                  <option value="Poor">Poor</option>
                  <option value="Very Poor">Very Poor</option>
                </select>
                <input required type="number" className="form-control" placeholder="Price" onChange={(elem) => this.setState({ Price: elem.target.value })} />
                <label className="switch">
                  <input type="checkbox" onChange={() => this.setState({ Bargain: !this.state.Bargain })} />
                  <span className="slider round"></span>
                </label>
                <label for="exampleFormControlSelect1">City</label>
                <select required className="form-control" id="exampleFormControlSelect1" onChange={(elem) => this.setState({ City: elem.target.value })}>
                  <option value="Bengaluru">Bengaluru</option>
                  <option value="Mumbai">Mumbai</option>
                  <option value="Chennai">Chennai</option>
                  <option value="Kolkata">Kolkata</option>
                  <option value="Vadodara">Vadodara</option>
                  <option value="Agra">Agra</option>
                  <option value="Delhi">Delhi</option>
                  <option value="Patna">Patna</option>
                </select>
                <button className="form-control btn btn-danger">AddProduct</button>
              </div>
            </form>
          }
        </div>
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  console.log("current state", state)
  return {
  }
}

const mapDispatchToProps = (dispatch) => {
  return bindActionCreators({ addProduct }, dispatch)
}


export default connect(mapStateToProps, mapDispatchToProps)(AddProduct)
