import React, { Component } from 'react';
import { connect } from "react-redux";
import { relatedVideos } from '../actions/actions';
import { bindActionCreators } from 'redux';

class VideoPlayer extends Component {
    render() {
        return (
            <div>
                <div className="row">
                    <div className="col-md-8 mt-4">
                        <iframe className="col-md-12 p-0" width="950" height="525" title="Title" src={"https://www.youtube.com/embed/6BYIKEH0RCQ?autoplay=1"} allow='autoplay;'>
                        </iframe>
                    </div>
                    <div className="col mt-4 p-0">
                        <div className="chat-replay col-md-11 mb-3">
                            SHOW CHAT REPLAY
                        </div>
                        <div className="row autoplayDiv mt-4">
                            <span className="text-white">Up next</span>
                            <div className="d-flex flex-col autoplay"> 
                            <span className="autoplayText mr-2">AUTOPLAY</span>
                                    <label className="switch">
                                        <input type="checkbox" defaultChecked={true} />
                                        <span className="slider round"></span>
                                    </label>
                            </div>
                        </div>
                            {this.props.videos.map((elem, index) => {
                                return <div className="row col-md-12 p-0">
                                    <div className="d-flex flex-col mt-2">
                                        <img className="col-md-5 p-0" height="110" src={elem.snippet.thumbnails.high.url} alt="" />
                                        <div className="col mt-2 thumbTitle text-white">
                                            {elem.snippet.title}
                                        </div>
                                    </div>

                                </div>
                            }
                            )}
                    </div>
                </div>
            </div>
        )
    }
    componentDidMount() {
        this.props.relatedVideos()
    }
}

const mapStateToProps = (state) => {
    console.log("landingpage state", state)
    return {
        videos: state.video.videos
    }
}

const mapDispatchToProps = (dispatch) => {
    return bindActionCreators({ relatedVideos }, dispatch)
}


export default connect(mapStateToProps, mapDispatchToProps)(VideoPlayer)
