let videosState = {
    searchHistory: [],
    videos: [],
    played: [],
    bookmarked: [],
    search: []
}

export function videosReducer(state = videosState, action) {
    console.log("videoReducer", videosState)
    let stateCopy = JSON.parse(JSON.stringify(state))
    switch (action.type) {
        case "Videos":
            stateCopy.search = action.payload
            return stateCopy
        case "relatedVideos":
            stateCopy.videos = action.payload
            return stateCopy
        case "ClearVids":
            stateCopy.search = []
            return stateCopy;
        default:
            return stateCopy
    }
}
