import React, { Component } from "react"
import { Redirect } from "react-router-dom"
import { connect } from 'react-redux';
import {login,logout} from '../ActionCreators/Action'
import {bindActionCreators} from 'redux'




class Login extends Component {

    state = { name: "mukhtar", email: "mukhtar@gmail.com" }

    signIn = () => {
        let userdata = {
            "user": {
                "name": this.state.name,
                "email": this.state.email,
            }
        }
        this.props.login(userdata)
    }

    // signOut = () => {
    //     this.props.logout()
    // }


    render() {

        if(this.props.isLoggedin){
            return(
                <Redirect to="/home"/>
            )
        }

        return (
            <div>
                <div className="row" style={{height:"100vh"}}>
                    <div className="col-md-4 bg-dark">
                        <h3 className="text-center text-white">Youtube music coming soon</h3>
                        <form action="" className="form-group mt-4">
                            <input className="form-control mt-2" type="text" placeholder="name" onChange={(elem) => this.setState({ name: elem.target.value })} />
                            <input className="form-control mt-2" type="password" placeholder="email" onChange={(elem) => this.setState({ email: elem.target.value })} />
                            <button className="btn btn-danger mt-2" onClick={() => this.signIn()}>Sign in</button>
                        </form>
                    </div>
                    <div className="col-md-6 bg-primary">

                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    console.log("full state", state)
    return {
        isLoggedin: state.auth.isLoggedin
    }
}

const mapDispatchToProps = (dispatch) => {
    return bindActionCreators({ login, logout }, dispatch)
}



