import React, { Component } from "react"
import { Redirect } from "react-router-dom"
import { connect } from 'react-redux';
import {login,logout} from '../actions/actions'
import {bindActionCreators} from 'redux'
import { Link } from "react-router-dom"



class Login extends Component {

    state = { name: "", email: "" }

    signIn = () => {
        let userdata = {
            "user": {
                "name": this.state.name,
                "email": this.state.email,
            }
        }
        this.props.login(userdata)
    }

    handleOnSubmit = (e)=>{
        e.preventDefault()
        this.signIn()
    }

    // signOut = () => {
    //     this.props.logout()
    // }


    render() {

        if(this.props.isLoggedin){
            return(
                <Redirect to="/home"/>
            )
        }

        return (
            <div>
                <div className="row" style={{"margin":"0", "overflow-x":"hidden"}}>
                    <div className="col-md-4 grey darken-4">
                        <br/>
                        <h3 className="text-center text-white"><i class="fa fa-youtube-play " aria-hidden="true"></i> Youtube Music is coming soon</h3>
                        <br/><br/>
                        <form onSubmit={this.handleOnSubmit} className="col-md-8 offset-md-2">
                            <input className="input-field mt-2" type="text" placeholder="Enter Name" onChange={(elem) => this.setState({ name: elem.target.value })} />
                            <input className="input-field mt-2" type="text" placeholder="Enter Email" onChange={(elem) => this.setState({ email: elem.target.value })}/>
                            <button className="btn red accent-4 mt-2" >Sign in</button>
                        </form>
                        <div className="font-weight-lighter text-white text-center mt-3">
                            Don't have an Account ? <Link>Register</Link>
                        </div>
                    </div>
                        <div className="col-md-6 p-0">
                            <img height="790px" src={require("./image-3.jpg")} alt=""/>
                        </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    console.log("full state", state)
    return {
        isLoggedin: state.auth.isLoggedin
    }
}

const mapDispatchToProps = (dispatch) => {
    return bindActionCreators({ login, logout }, dispatch)
}


export default connect(mapStateToProps, mapDispatchToProps)(Login)
