import React, { Component } from 'react';
import { connect } from "react-redux";
import { relatedVideos } from '../actions/actions';
import { bindActionCreators } from 'redux';

class Recommended extends Component {


    render() {
        return (
            <div>
                        <div className="mt-2">
                            <img height="300px" width="100%" src={require("./apple.jpg")} alt="" />
                        </div>
                        <div className="m-4"><h5 className="font-weight-bold text-white">Recommended</h5></div>
                        <div className="col-md-12">
                            <div className="row">
                                {this.props.videos.map((elem, index) => {
                                    return <div className="col-md-3 mb-5">
                                        <div className="p-2 thumbDiv">
                                            <div className="thumbPlay"><i class="fa fa-play fa-2x" aria-hidden="true"></i></div>
                                            <img width="100%" src={elem.snippet.thumbnails.high.url} alt="" />
                                        </div>
                                        <div className="col-md-11 thumbTitle">
                                            <small className="font-weight-bold text-white">{elem.snippet.title}</small>
                                        </div>
                                    </div>
                                }
                                )}
                            </div>
                        </div>
                    </div>
        )
    }
    componentDidMount() {
        this.props.relatedVideos()
    }
}

const mapStateToProps = (state) => {
    console.log("landingpage state", state)
    return {
        videos: state.video.videos
    }
}

const mapDispatchToProps = (dispatch) => {
    return bindActionCreators({ relatedVideos }, dispatch)
}


export default connect(mapStateToProps, mapDispatchToProps)(Recommended)
