import axios from "axios"

export function login(data) {
    let userdata = axios({
        method: "post",
        url: "http://localhost:3020/users",
        data: data
    })
    return (dispatch)=> {
        userdata.then(res => {
            dispatch({
                type: "Login",
                payload: res.data.data[0]
            })
        })
    }
}

export function signup(data) {
    let userdata = axios({
        method: "post",
        url: "http://localhost:3020/users/signup",
        data: data
    })
    return (dispatch)=> {
        userdata.then(res => {
            dispatch({
                type: "SignUp",
                payload: res.data.data
            })
        })
    }
}

export function logout(){
    return ({
        type: "Logout"
    })
}
