let videosState = {
    searchHistory: [],
    videos: [],
    played: [],
    bookmarked: []
}

 export function videosReducer(state = videosState, action) {
    //  let stateCopy = JSON.parse(JSON.stringify(state))
    switch (action.type) {
        case "Videos":
            state.videos = action.payload
            state.searchHistory = action.payload
        return state
        default:
            return state
    }
}
