import React, { Component } from 'react';
import { connect } from "react-redux";
import { relatedVideos } from '../actions/actions';
import { bindActionCreators } from 'redux';

class VideoPlayer extends Component {
    render() {
        return (
            <div>
                <div className="row">
                    <div className="col-md-8 mt-4 ml-3">
                        <iframe className="col-md-12" height="518" title="Title" src={"https://www.youtube.com/embed/6BYIKEH0RCQ?autoplay=1"} allow='autoplay;'>
                        </iframe>
                    </div>
                    <div className="col mt-3">
                        <ul className="list-unstyled">
                            <div>
                                <span className="text-white">Up next</span>
                                <span className="autoplay float-right mr-4">AUTOPLAY</span>
                            </div>
                            {this.props.videos.map((elem, index) => {
                                return <div className="row col-md-12">
                                    <div className="d-flex flex-col mt-2">
                                        <img className="col-md-6" height="110" src={elem.snippet.thumbnails.high.url} alt="" />
                                        <div className="col-md-4 mt-2 thumbTitle text-white">
                                            {elem.snippet.title}
                                        </div>
                                    </div>

                                </div>
                            }
                            )}
                        </ul>
                    </div>
                </div>
            </div>
        )
    }
    componentDidMount() {
        this.props.relatedVideos()
    }
}

const mapStateToProps = (state) => {
    console.log("landingpage state", state)
    return {
        videos: state.video.videos
    }
}

const mapDispatchToProps = (dispatch) => {
    return bindActionCreators({ relatedVideos }, dispatch)
}


export default connect(mapStateToProps, mapDispatchToProps)(VideoPlayer)
