import React, { Component } from 'react';
import { connect } from "react-redux";
// import { fetchV } from '../actions/actions';
// import { bindActionCreators } from 'redux';

class SearchList extends Component {

    state = {
        displayVids : false
    }
    render() {
        return (
            <div>
                <ul className={"list-unstyled"+(this.state.displayVids?"":"d-none")}>
                    {this.props.search.map((elem, index) => {
                        return <li className="col-md-3 mb-5">
                            <div className="p-2 thumbDiv">
                                <div className="thumbPlay"><i class="fa fa-play fa-2x" aria-hidden="true"></i></div>
                                <img width="100%" src={elem.snippet.thumbnails.high.url} alt="" />
                            </div>
                            <div className="col-md-11 thumbTitle">
                                <small className="font-weight-bold text-white">{elem.snippet.title}</small>
                            </div>
                        </li>
                    }
                    )}
                </ul>
            </div>
        )
    }
    componentDidUpdate(prevProps){
        if(prevProps.search!=this.props.search){
            this.setState({
                displayVids: true
            })
        }
    }
}

const mapStateToProps = (state) => {
    console.log("searchpage state", state)
    return {
        search: state.video.search
    }
}

export default connect(mapStateToProps)(SearchList)
