let videosState = {
  nowPlayingID: "",
  videoData: [],
  searchHistory: [],
  trending: [],
  videos: [],
  played: [],
  bookmarked: [],
  search: []
};
export function videosReducer(state = videosState, action) {
  console.log("videoReducer", videosState);
  let stateCopy = JSON.parse(JSON.stringify(state));

  switch (action.type) {
    case "videoDataReq":
      stateCopy.videoData = action.payload;
      return stateCopy;

    case "SetVidID":
      stateCopy.nowPlayingID = action.payload;
      return stateCopy;

    case "Videos":
      stateCopy.search = action.payload;
      return stateCopy;

    case "relatedVideos":
      stateCopy.videos = action.payload;
      return stateCopy;

    case "trendingVids":
      stateCopy.trending = action.payload;
      return stateCopy;

    case "ClearVids":
      stateCopy.search = [];
      stateCopy.videos = [];
      return stateCopy;

    default:
      return stateCopy;
  }
}
