import React, { Component } from 'react';
import { connect } from "react-redux"
import { login, logout } from "./actions/actions"
import { bindActionCreators } from "redux"

class App extends Component {
  state = { name: "mukhtar", email: "mukhtar@gmail.com" }

  signIn = () => {
    let userdata = {
      "user": {
        "name": this.state.name,
        "email": this.state.email,
      }
    }
    this.props.login(userdata)
  }

  signOut = ()=> {
    this.props.logout()
  }

  render() {
    return (
      <div>
        {
          !this.props.isLoggedin && (
            <div>
              <input type="text" placeholder="name" onChange={(elem) => this.setState({ name: elem.target.value })} />
              <input type="text" placeholder="email" onChange={(elem) => this.setState({ email: elem.target.value })} />
              <button onClick={() => this.signIn()}>sign in</button>
              <button onClick={() => this.signOut()}>sign out</button>
            </div>
          )
        }
        {
          this.props.isLoggedin && (
            <div>
              you're logged in!
            </div>
          )
        }
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  console.log("full state", state)
  return {
    isLoggedin: state.auth.isLoggedin
  }
}

const mapDispatchToProps = (dispatch) => {
  return bindActionCreators({ login, logout }, dispatch)
}

export default connect(mapStateToProps, mapDispatchToProps)(App)
