export function fetchVideos(relatedVids) {
    let request = axios({
        method: "get",
        url: `https://www.googleapis.com/youtube/v3/search?part=snippet&relatedToVideoId=5rOiW_xY-kc&type=video&key=${Api_Key}`,
        data: relatedVids
    })
    return (dispatch) => {
        request.then(response => {
            console.log(response)
            dispatch({
                type: "Videos",
                payload: response.data.items
            })
        }).catch()
    }
}



export function login(data) {
    let userdata = axios({
        method: "post",
        url: "http://localhost:3020/users",
        data: data
    })
    return (dispatch) => {
        userdata.then(res => {
            dispatch({
                type: "Login",
                payload: res.data.data[0]
            })
        })
    }
}

export function signup(data) {
    let userdata = axios({
        method: "post",
        url: "http://localhost:3020/users/signup",
        data: data
    })
    return (dispatch) => {
        userdata.then(res => {
            dispatch({
                type: "SignUp",
                payload: res.data.data
            })
        })
    }
}

export function logout() {
    return ({
        type: "Logout"
    })
}
