import React, { Component } from "react";
import Videoplayer from "./videoplayer"


class Home extends Component {

    state = {
        title: 'ritviz',
    }

    titleChange(event){
        this.setState({ 
            title: event.target.value
    },()=>console.log(this.state))   
    }

    sentSearchVidsToList(){
        this.props.getSearchedVids(this.state.title)
        this.setState({
            title: ""
        })
    }
    render() {
        return (
            <div>
                <div className="col-md-8 d-flex flex-col">
                    <i class="fa fa-youtube-play fa-2x text-danger float-left" aria-hidden="true"></i><strong className='mt-1'>YouTube</strong>
                    <input className="form-control offset-md-3" onChange={(event) => this.titleChange(event)} value={this.state.title} type="search" placeholder="Search for a video"></input>
                    <button className="btn btn-danger" onClick={() => { this.sentSearchVidsToList() }}>Search</button>
                </div>
                <Videoplayer/>
            </div>
        )
    }
    componentDidMount() {
        this.sentSearchVidsToList()
    }

}

export default Home
