import React, { Component } from 'react';
import { connect } from "react-redux"
import axios from "axios"

class App extends Component {
  state = { name: "mukhtar", email: "mukhtar@gmail.com" }

  signIn = () => {
    let user = axios({
      method: "post",
      url: "http://localhost:3020/users",
      data: { 
        "user":{ 
          "name": this.state.name,
          "email": this.state.email,
          "mobile": 808080800
        }}
    })
    user.then(res=>{
      console.log("user res",res)
        // this.props.dispatch({
        //   type: "Login",
        //   payload: res.data.data[0]
        // })
    }).catch((err, res)=>{
        console.log("error",err)
    })
  }
  render() {
    return (
      <div>
        {
          !this.props.isLoggedin && (
            <div>
              <input type="text" placeholder="name" onChange={(elem) => this.setState({ name: elem.target.value })} />
              <input type="text" placeholder="email" onChange={(elem) => this.setState({ email: elem.target.value })} />
              <button onClick={() => this.signIn()}>sign in</button>
            </div>
          )
        }
        {
          this.props.isLoggedin && (
            <div>
              you're logged in!
            </div>
          )
        }
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  console.log("full state", state)
  return {
    isLoggedin: state.auth.isLoggedin
  }
}

export default connect(mapStateToProps)(App)
